import { preview } from '@/utils/file-uploader';
import { Image } from 'antd';
import React, { useEffect, useState } from 'react';
import Draggable from 'react-draggable';
import { Document, Page, pdfjs } from 'react-pdf';
import 'react-pdf/dist/Page/AnnotationLayer.css';
import 'react-pdf/dist/Page/TextLayer.css';
import PdfPreviewToolbar from './PdfPreviewToolbar';

interface Props {
  url?: string;
}

pdfjs.GlobalWorkerOptions.workerSrc = new URL(
  'pdfjs-dist/build/pdf.worker.min.js',
  import.meta.url,
).toString();

const PdfPreview: React.FC<Props> = (props) => {
  const [numPages, setNumPages] = useState<number>(0);
  const [pageNumber, setPageNumber] = useState<number>(1);
  const [scale, setScale] = useState<number>(0.8);
  const [file, setFile] = useState<File | null>();

  const onDocumentLoadSuccess = ({ numPages }: { numPages: number }): void => {
    setNumPages(numPages);
  };
  const handleBack = () => {
    if (pageNumber <= 1) return false;
    else {
      setPageNumber(pageNumber - 1);
    }
  };
  const handleForward = () => {
    if (!numPages) return false;
    if (pageNumber >= numPages) return false;
    else {
      setPageNumber(pageNumber + 1);
    }
  };
  const handleShrink = () => {
    if (scale <= 0.1) return false;
    else {
      setScale(scale - 0.1);
    }
  };
  const handleAlt = () => {
    if (scale >= 5) return false;
    else {
      setScale(scale + 0.1);
    }
  };
  useEffect(() => {
    preview(props.url, setFile);
  }, []);
  if (!props.url) return <div>url无效</div>;
  return (
    <Image
      width={100}
      height={50}
      fallback="data:image/png;base64,"
      src="error"
      preview={{
        imageRender: () => {
          return (
            <Draggable>
              <div>
                <Document file={file} onLoadSuccess={onDocumentLoadSuccess}>
                  <Page pageNumber={pageNumber} scale={scale} />
                </Document>
              </div>
            </Draggable>
          );
        },
        toolbarRender: () => {
          return (
            <PdfPreviewToolbar
              handleBack={handleBack}
              handleForward={handleForward}
              handleShrink={handleShrink}
              handleAlt={handleAlt}
              pageNumber={pageNumber}
              numPages={numPages}
            />
          );
        },
      }}
    />
  );
};

export default PdfPreview;
